import React, { Component, useEffect, useState } from "react";
import "./App.css";
import Recipe from "./recipes";

let App = () => {
  const App_ID = "fd66710f",
    App_Key = "b730ff582098b26929d3ced30bdb1a62	",
    ExReq = `https://api.edamam.com/api/recipes/v2?type=public&q=chicken&app_id=${App_ID}&app_key=${App_Key}`;
  const [recipe, setRecipe] = useState([]);

  useEffect(() => {
    getRecipes();
  });

  const getRecipes = async () => {
    const res = await fetch(ExReq);
    const data = await res.json();
    setRecipe(data.hits);
    console.log(recipe);
  };

  return (
    <div>
      <h1>Hello, World</h1>
      <input
        type="text"
        placeholder="What is recipe you need to query for ? ..."
      />
      <button>Query</button>

      {recipe.map((recipe) => {
        <Recipe title={recipe.recipe.label} />;
      })}
    </div>
  );
};
export default App;
