import React, { Component, useEffect, useState } from "react";
import "./App.css";
import Recipe from "./recipes";

let App = () => {
  const App_ID = "fd66710f",
    App_Key = "b730ff582098b26929d3ced30bdb1a62	",
    ExReq = `https://api.edamam.com/api/recipes/v2?type=public&q=chicken&app_id=${App_ID}&app_key=${App_Key}`;
  const [recipes, setRecipe] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    getRecipes();
  });

  const getRecipes = async () => {
    const res = await fetch(ExReq);
    const data = await res.json();
    setRecipe(data.hits);
  };

  return (
    <div>
      <h1>Hello, World</h1>
      <input
        className="search-bar"
        type="text"
        placeholder="What is recipe you need to query for ? ..."
      />
      <button>Query</button>

      {recipes.map((recipe) => (
        <Recipe
          key={recipe.recipe.label}
          title={recipe.recipe.label}
          calories={recipe.recipe.calories}
          image={recipe.recipe.image}
        />
      ))}
    </div>
  );
};
export default App;
